// commonJS spec

console.log('-greet/index.js-');

var en = require('./en');
var es = require('./es');

function greet(lang) {
    if (lang === 'en') {
        en();return;
    }
    if (lang === 'es') {
        es();return;
    }
    console.log('provide lang input for customized greetings');
}

module.exports = {
    greet
};